import CreatePost from './pages/CreatePost'
import EditPost from './pages/EditPost'
import { Link } from 'react-router-dom'

const App = () => {

  const posts = [
    {
        'id': '1',
        'title': 'Cartwheel in Chelsea ü§∏üèΩ‚Äç‚ôÄÔ∏è',
        'author': 'John Smith',
        'description': 'A thrilling cartwheel through the vibrant streets of Chelsea. Witness the fusion of athleticism and urban charm as we explore the heart of this iconic neighborhood.'
    },
    {
        'id': '2',
        'title': 'Love Lock in Paris üîí',
        'author': 'Robert Johnson',
        'description': 'A romantic journey to Paris, where lovers from around the world attach locks to the famous bridges as a symbol of their enduring affection. Discover the stories behind these love locks and the beautiful city that hosts them.'
    },
    {
        'id': '3',
        'title': 'Wear Pink on Fridays üéÄ',
        'author': 'Michael Brown',
        'description': 'Join the fun tradition of wearing pink on Fridays to show solidarity and support. Explore the origins of this trend and see how people embrace it with style and enthusiasm.'
    },
    {
        'id': '4',
        'title': 'Adopt a Dog üê∂',
        'author': 'James Williams',
        'description': 'A heartwarming guide to adopting a dog. Learn about the joys and responsibilities of bringing a furry friend into your life, and find out how to choose the perfect companion for your family.'
    },
];



  // Sets up routes
  let element = useRoutes([
    {
      path: "/",
      element:<ReadPosts data={posts}/>
    },
    {
      path:"/edit/:id",
      element: <EditPost data={posts} />
    },
    {
      path:"/new",
      element: <CreatePost />
    }
  ]);

  return ( 

    <div className="App">

      <div className="header">
        <h1>üëç Bet 1.0</h1>
        <Link to="/"><button className="headerBtn"> Explore Challenges üîç  </button></Link>
        <Link to="/new"><button className="headerBtn"> Submit Challenge üèÜ </button></Link>
      </div>
        {element}
    </div>

  );
}

export default App;
